# Vercel Environment Variables Setup# Simple script to guide environment variable setup# Set encoding$OutputEncoding = [System.Text.Encoding]::UTF8[Console]::OutputEncoding = [System.Text.Encoding]::UTF8Write-Host "========================================" -ForegroundColor CyanWrite-Host "  Vercel Environment Setup" -ForegroundColor CyanWrite-Host "========================================" -ForegroundColor CyanWrite-Host ""# Check Vercel CLIif (-not (Get-Command "vercel" -ErrorAction SilentlyContinue)) {    Write-Host "[ERROR] Vercel CLI not installed!" -ForegroundColor Red    Write-Host "Install: npm install -g vercel" -ForegroundColor Yellow    exit 1}Write-Host "[OK] Vercel CLI installed" -ForegroundColor GreenWrite-Host ""# Get MongoDB URIWrite-Host "1. MongoDB Connection String" -ForegroundColor YellowWrite-Host "   Example: mongodb+srv://user:pass@cluster.mongodb.net/dbname" -ForegroundColor Gray$mongoUri = Read-Host "   Enter MONGODB_URI"if ([string]::IsNullOrWhiteSpace($mongoUri)) {    Write-Host "[ERROR] MONGODB_URI is required!" -ForegroundColor Red    exit 1}# Get DB Name$dbName = Read-Host "   Enter DB_NAME (default: busung_hr)"if ([string]::IsNullOrWhiteSpace($dbName)) {    $dbName = "busung_hr"}Write-Host ""# JWT SettingsWrite-Host "2. JWT Configuration" -ForegroundColor Yellow$jwtSecret = Read-Host "   Enter JWT_SECRET (leave empty to auto-generate)"if ([string]::IsNullOrWhiteSpace($jwtSecret)) {    $jwtSecret = -join ((65..90) + (97..122) + (48..57) | Get-Random -Count 32 | ForEach-Object {[char]$_})    Write-Host "   Auto-generated: $jwtSecret" -ForegroundColor Gray}$jwtExpires = Read-Host "   Enter JWT_EXPIRES_IN (default: 24h)"if ([string]::IsNullOrWhiteSpace($jwtExpires)) {    $jwtExpires = "24h"}Write-Host ""Write-Host "========================================" -ForegroundColor CyanWrite-Host ""# SummaryWrite-Host "Configuration Summary:" -ForegroundColor CyanWrite-Host "  MONGODB_URI: $($mongoUri.Substring(0, [Math]::Min(50, $mongoUri.Length)))..." -ForegroundColor WhiteWrite-Host "  DB_NAME: $dbName" -ForegroundColor WhiteWrite-Host "  JWT_SECRET: $($jwtSecret.Substring(0, 10))..." -ForegroundColor WhiteWrite-Host "  JWT_EXPIRES_IN: $jwtExpires" -ForegroundColor WhiteWrite-Host ""$confirm = Read-Host "Continue? (Y/n)"if ($confirm -eq "n" -or $confirm -eq "N") {    Write-Host "Cancelled." -ForegroundColor Yellow    exit 0}Write-Host ""Write-Host "========================================" -ForegroundColor YellowWrite-Host "  Manual Steps Required" -ForegroundColor YellowWrite-Host "========================================" -ForegroundColor YellowWrite-Host ""Write-Host "First, link to Vercel project:" -ForegroundColor CyanWrite-Host "  vercel link" -ForegroundColor WhiteWrite-Host ""Write-Host "Then run these commands:" -ForegroundColor CyanWrite-Host ""# Save to temp file for easy copy$commands = @"# Copy and paste these commands one by one:vercel env add MONGODB_URI production preview development# When prompted, paste: $mongoUrivercel env add DB_NAME production preview development# When prompted, paste: $dbNamevercel env add JWT_SECRET production preview development# When prompted, paste: $jwtSecretvercel env add JWT_EXPIRES_IN production preview development# When prompted, paste: $jwtExpires"@Write-Host $commands -ForegroundColor WhiteWrite-Host ""# Save to file$commands | Out-File "vercel-env-commands.txt" -Encoding UTF8Write-Host "Commands saved to: vercel-env-commands.txt" -ForegroundColor GrayWrite-Host ""Write-Host "========================================" -ForegroundColor GreenWrite-Host "  Next Steps:" -ForegroundColor GreenWrite-Host "========================================" -ForegroundColor GreenWrite-Host ""Write-Host "1. Run: vercel link" -ForegroundColor WhiteWrite-Host "2. Copy commands from vercel-env-commands.txt" -ForegroundColor WhiteWrite-Host "3. Run: npm run deploy:preview" -ForegroundColor WhiteWrite-Host ""